BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS `lieu` (
	`lieu_id`	INTEGER NOT NULL,
	`label`	TEXT NOT NULL,
	PRIMARY KEY(`lieu_id`)
);
INSERT INTO `lieu` VALUES (1,'Paris');
INSERT INTO `lieu` VALUES (2,'Vincennes');
INSERT INTO `lieu` VALUES (3,'Croisset');
